    public int calculateBinaryTreeLayer(TreeNode node) {
        if (node == null) {
            return 0;
        }
        //‰∏çÁÆ°ÊÄé‰πàÊ†∑ÈÄöÂ∏∏bfsÊòØË¶ÅËøõÈòüÁöÑ
        Queue<TreeNode> queue = new LinkedList<>();
        queue.add(node);
        int count = 0;

        while (!queue.isEmpty()) {
            List<TreeNode> polls = new ArrayList<>();
            //Ëøô‰∏™ÊòØÂèñÂá∫ÂΩìÂâçËøô‰∏ÄÂ±ÇÊâÄÊúâÁöÑÂ∑¶Âè≥ËäÇÁÇπ ÂÆûÈôÖ‰∏äÈíàÂØπÂ§öÂèâÊ†ë ÂÖ∂ÊÄùÊÉ≥‰πüÊòØ‰∏ÄÊ†∑
            while (!queue.isEmpty()) {
                polls.add(queue.poll());
            }

            //ÊääÊâÄÊúâËäÇÁÇπÁöÑÂ≠êËäÇÁÇπÂÜçÂ°ûÂõûÂéª
            polls.forEach(poll -> {
                if (poll.right != null) {
                    queue.add(poll.right);
                }
                if (poll.left != null) {
                    queue.add(poll.left);
                }
            });

            //ËÆ°Êï∞+1
            count++;
        }
        return count;
    }

